package net.mccreators.animod.block.custom;import net.mccreators.animod.block.ModBlocks;import net.minecraft.core.BlockPos;import net.minecraft.core.Direction;import net.minecraft.world.item.AxeItem;import net.minecraft.world.item.context.UseOnContext;import net.minecraft.world.level.BlockGetter;import net.minecraft.world.level.block.RotatedPillarBlock;import net.minecraft.world.level.block.state.BlockState;import net.minecraftforge.common.ToolAction;import org.jetbrains.annotations.Nullable;public class ModFlammableRotatedPillarBlock extends RotatedPillarBlock {    public ModFlammableRotatedPillarBlock(Properties properties) {        super(properties);    }    @Override    public @Nullable BlockState getToolModifiedState(BlockState state, UseOnContext context, ToolAction toolAction, boolean simulate) {        if (context.getItemInHand().getItem() instanceof AxeItem) {            if (state.is(ModBlocks.WIP_LOG.get())) {                return ModBlocks.STRIPPED_WIP_LOG.get().defaultBlockState().setValue(AXIS, state.getValue(AXIS));            }            if (state.is(ModBlocks.WIP_WOOD.get())) {                return ModBlocks.STRIPPED_WIP_WOOD.get().defaultBlockState().setValue(AXIS, state.getValue(AXIS));            }        }        return super.getToolModifiedState(state, context, toolAction, simulate);    }}